import React from 'react'
import styled from 'styled-components'
function AddPost() {
    const onSubmit = () => {}

    const AddBlock = styled.div`
    background:white;
    width:512px;
    height:768px;
    border-radius:16px;
    margin-top:96px;
    margin-bottom:32px;
    display:flex;
    flex-direction:column;
    border:2px solid lightgray;
    margin: auto auto;
    .HeaderBlock {
        position:absolute;
    }
    .FormBlock {
        .form {
            display:flex;
            width:496px;
            flex-direction:column;
            
        }
        .input {

        }
    }
    
    
    `
    return (
        <AddBlock>
            <div className="HeaderBlock">
                <h2 className="h2">글쓰기</h2>
            </div>
            <div className="FormBlock">
                <form className="form" onSubmit={onSubmit}>
                    <input className="input" type="text" name="title" required placeholder="제목을 입력하세요"/>
                    <textarea className="textarea"type="textarea" maxLength="140" rows="7" placeholder="내용을 입력하세요"></textarea>
                    <button className="btn" type="button" name="submit">Add Post</button>
                </form>
            </div>
        </AddBlock>
    )
}
export default AddPost
