import React,{useState} from 'react'
import styled from 'styled-components'

function Comment() {

    const CommentBlock = styled.div`
        width:768px;
        display:flex;
        flex-direction:column;
        margin: 0 auto;
        @media(max-width:767px) {
            width:512px;
        
            
        }
        @media(max-width:540px) {
            margin-left:20px;
            margin-right:20px;
            margin-top:20px;
            
        }
        .Background {
            padding:
            .titleandDelete{
                display:flex;
                justify-content:space-between;
                align-items:center;
                margin-top:10px;
                .itemTitle {
                    color:rgba(0,0,0,0.84);
                    font-size:32px;
                    line-height:40px;
                    font-weight:400;
                    margin-top:10px;
                }
            }
        }
            
     `  
    return (
        <>
            <CommentBlock>
                <div className="Background">
                    <div className="formblock">
                        <form className="form">
                            <div className="inputblock">
                                <input type="text" name="name" className="input" placeholder="필명" />
                                <input type="text" name="password" className="input" placeholder="확인번호" />
                            </div>
                            <textarea className="textarea" placeholder="댓글"></textarea>
                        </form>
                    </div>
                </div>
            </CommentBlock>
        </>

    )
}
export default Comment
