import styled from 'styled-components'
import axios from 'axios'
import Header from './Header.js'

function PostPage({match}) {
    
    const {id,title,body} = match.params
    const PostPageBlock = styled.div`
        position:absoulte;
        margin: 0 24px;
        width:100%;
        
        .title {
            color:rgba(0,0,0,0.84);
            font-size:30px;
            line-height:40px;
            font-weight:400;
        }
        .body {

        }

    `

    return (
        <>
            <Header />
            <PostPageBlock>
                <div className="title">
                    <h1 className="itemTitle">
                        {title}
                    </h1>
                </div>
                <div className="body">

                </div>
            </PostPageBlock>


        </>
        
    )   
}

export default PostPage
