import React , {useState,useEffect} from 'react'
import styled from 'styled-components'
import axios from 'axios'
import Header from './Header.js'
import tumb from './image/tumb.PNG'
import instablack from './image/instablack.PNG'
import facebook from './image/facebook.PNG'
import user2 from './image/user2.png'
const fetchURL = 'http://localhost:5000/getPost'

function PostPage({match}) {
    const [items,SetItems] = useState()
    const [input,SetInput] = useState({title:'',body:''})
    const {title,body} = input

    useEffect(()=> {
        axios.post(fetchURL).then(res=> {
            console.log(res)
            SetItems(res.data)
            if(res.data._id === match.params.id) {
                console.log(res.data._id)
            }
        })
    },[])
    
    const getData = () => {
        items.map(item=> {
            if(item._id === match.params.id){
                SetInput({
                    ...input,
                    title:item.title,
                    body:item.body
                })
            } 
        })
    }
    const PostPageBlock = styled.div`
        display:flex;
        justify-content:center;
        align-items:center;
        width:100%;
        background:white;
        box-shadow: 0px -2px -4px 4px #eee;

        .PageBlock {
            width:768px;
            @media(max-width:767px) {
                width:512px
                
            }
            @media(max-width:400px) {
                margin-left:10px;
            }
            display:flex;
            flex-direction:column;
            .title {
                .itemTitle {
                    color:rgba(0,0,0,0.84);
                    margin:0;
                    font-size:35px;
                    line-height:40px;
                    font-weight:400;
                }
                .LogoWithIntro {
                    display:flex;
                    margin-top:10px;
                    justify-content:space-between;
                    align-items:center;
                    @media(max-width:768px) {
                        flex-direction:column-reverse;
                        align-items:stretch;
                    }
                    .Intro {
                       display:flex;
                    }
                    .Logo {
                        display:flex;
                        .Logo1 {
                            padding-right:8px;
                            cursor:pointer;
                        }
                        .insta {
                            
                        }
                    }
                        
                    
                }
            }
            .body {

            }
        }
    `

    return (
        <>
            <Header />
            <PostPageBlock>
                {items
                    ? items.map(item => {
                        if(item._id===match.params.id) {
                            return (
                                <div className="PageBlock">
                                    <div className="title">
                                        <h1 className="itemTitle">
                                            {item.title}
                                        </h1>
                                        <div className="LogoWithIntro">
                                            <div className="Intro">
                                                
                                                <img size ="10" src={user2} />
                                                <p className="name">김준영</p>
                                                
                                            </div>
                                            <div className ="Logo">
                                                <p className=" Logo1 insta" onClick={() => {window.location.assign("https://www.instagram.com/c_mongkey/?hl=ko")}}>
                                                    <img size ="10" src={instablack} alt="인스타" />
                                                </p>
                                                <p className="Logo1 facebook" onClick={() => {window.location.assign("https://www.facebook.com")}}>
                                                    <img size ="10" src={facebook} alt="페이스북" />
                                                </p>
                                                <p className="Logo1 twitter" onClick={() => {window.location.assign("https://www.tumblr.com/blog/junyoung110707")}}>
                                                    <img size ="10" src={tumb} alt="텀블러" />
                                                </p>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="body">
                                        {body}
                                    </div>
                                </div>
                            )
                        }
                    })
                    : "Loading"
                }
            </PostPageBlock>


        </>
        
    )   
}

export default PostPage
