import React,{useState} from 'react'
import styled from 'styled-components'

function Comment() {

    const CommentBlock = styled.div`
        width:768px;
        display:flex;
        flex-direction:column;
        margin: 0 auto;
        background:#f1f4f8;
        border: 1px solid #DADDE0;
        @media(max-width:767px) {
            width:512px;
        
            
        }
        @media(max-width:540px) {
            width:420px;
            margin-left:20px;
            margin-right:20px;
            margin-top:20px;
            
        }
        .Background {
            display:flex;
            padding:20px;
            
            .form {
                .inputblock {
                    display:flex;
                    justify-content:space-between;
                    .input {
                        padding: 0 11px;
                    }
                }
                .textarea {

                }
            }
           

        }

    `
    return (
        <>
            <CommentBlock>
                <div className="Background">
                    <form className="form">
                        <div className="inputblock">
                            <input type="text" name="name" className="input" placeholder="필명" />
                            <input type="text" name="password" className="input" placeholder="확인번호" />
                        </div>
                        <textarea className="textarea" placeholder="댓글" style={{resize:'none'}} />
                    </form>
                </div>
            </CommentBlock>
        </>

    )
}
export default Comment
