import React , {useState,useEffect} from 'react'
import styled from 'styled-components'
import axios from 'axios'
import Header from './Header.js'
const fetchURL = 'http://localhost:5000/getPost'

function PostPage({match}) {
    const [items,SetItems] = useState()
    const [input,SetInput] = useState({title:'',body:''})
    const {title,body} = input

    useEffect(()=> {
        axios.post(fetchURL).then(res=> {
            console.log(res)
            SetItems(res.data)
        })
    },[])
    
    const getData = () => {
        items.map(item=> {
            if(item._id === match.params.id){
                SetInput({
                    ...input,
                    title:item.title,
                    body:item.body
                })
            } 
        })
    }
    const PostPageBlock = styled.div`
        display:flex;
        justify-content:center;
        align-items:center;
        width:100%;
        background:#f9f9f9;
        box-shadow: 0px -2px -4px 4px #eee;

        .PageBlock {
            width:768px;
            @media(max-width:767px) {
                width:512px
            }
            display:flex;
            flex-direction:column;
            .title {
                .itemTitle {
                    color:rgba(0,0,0,0.84);
                    margin:0;
                    font-size:30px;
                    line-height:40px;
                    font-weight:400;
                }
            }
            .body {

            }
        }
    `

    return (
        <>
            <Header />
            <PostPageBlock>
                <div className="PageBlock">
                    <div className="title">
                        <h1 className="itemTitle" onClick={getData}>
                            click{title}
                        </h1>
                        <span className ="Logo">

                        </span>
                    </div>
                    <div className="body">
                        {body}
                    </div>
                </div>
            </PostPageBlock>


        </>
        
    )   
}

export default PostPage
