import React ,{useState} from 'react'
import axios from 'axios'
import styled from 'styled-components'
function SignUp() {
    const [input,SetInput] = useState({nickname:'',email:'',password:''})
    const {name ,email,password } = input
    const onChange = e => {
        const {value,name} = e.target
        SetInput({
            ...input,
            [name]:value
        })

    }
    const onSubmit = e => {
        e.preventDefault()
    }
    const Sign = () => {
        axios.post('http://localhost:5000/Sign',{
            name:'name',
            email:'email',
            password:'password'
        }).then(res=>console.log(res.data)).catch(e=>console.log(e.response))
    }
    const SigninBlock = styled.div`
        width:512px;
        height:760px;
        position:relative;
        background:white;
        border-radius:16px;
        bix-shadow:0 0 8px rgba(0,0,0,0.04);
        margin:0 auto;
        margin-top:96px;
        margin-bottom:32px;
        display:flex;
        flex-direction:column;
        .Header {
            padding:48px 32px 24px 32px;
            border-bottom:1px solid #e9ecef;

            .H1 {
                margin:0;
                font-size:36px;
                color:#343940;
            }
            .text {
                font-size: 18px;
                color:#206997;
                margin-top:40px;
                font-weight:bold;

            }
        }
        .Section {
            flex:1;
            padding:30px 30px;
            padding-bottom:48px;
            overflow-y:auto;
            .Inner {
                .form {
                    display:flex;
                    flex-direction:column;    
                    .label {
                        font-size:16px;
                        font-family: 'Roboto', sans-serif;
                    }
                    .input {
                        margin-top:10px;
                        padding:6px;
                        border-radius:4px;
                        border:1px solid #dee2e6;
                        outline:none;
                        font-size:18px;
                        box-sizing:border-box;
                        &:hover {
                            color:#206997;
                        }
                    }
                    .btn {
                        
                    }
                }
            }
        }
    `
    return (
        <SigninBlock>
            <div className="Header">
                <h1 className="h1">Sign Up</h1>
                <span className="text">회원가입</span>
            </div>
            <div className="Section">
                <div className="Inner">
                    <form className="form" onSubmit={onSubmit}>
                        <label for ="inputemail" className="label">name{name}</label>
                        <input 
                            name ="name" 
                            className="input"
                            onChange ={onChange}
                            value={name}
                            placeholder="name"
                            required autofocus />
                        <label for ="inputemail" className="label">Email address {email}</label>
                        <input 
                            name ="email" 
                            className="input"
                            onChange ={onChange}
                            value={email}
                            placeholder="email"
                             />
                        <label for ="inputpassword" className="label">Password {password}</label>
                        <input 
                            className="input"
                            name="password"
                            onChange ={onChange}
                            value={password}
                            placeholder="password"
                        />
                        <button type="button" className="btn" onClick={Sign}>Sign Up</button>
                    </form>
                </div>
            </div>
        </SigninBlock>
    )
}
export default SignUp
