import React from 'react'
import styled from 'styled-components'
import {Link} from 'react-router-dom'
import instagram from './image/instagram.png'
import user from './image/user.png'
const PageBlock = styled.div`
	position:fixed;
	z-index:300;
	width:100%;
	height:100%;
	opacity:0.8;
	transition:0.8s;
	transform:${prop=>prop.Toggle ? 'translateX(0%)' : ' translateX(-100%)'}
`
const SidePageBlock = styled.div`
	width:50%;
	height:100%;
	background:#f1f3f5;
	@media(max-width:768px) {
		z-index:300;
		background:#f8f8f8;
	}
	
`
const SideBox = styled.div`
	width:80%;
	margin:0 auto;
	margin-left:20px;
`
const UlBlock = styled.div`
	display:flex;
	flex-direction:column;
	.list-item {
		font-size:14px;
		font-family: 'Nanum Myeongjo', serif;
		line-height:11px;
		cursor:pointer;
		color:black;
		border-bottom:1px solid lightgray;
		padding: 30px 5px;
		@media(max-width:768px) {
			font-weight:bold;
			font-size:15px;
			border:none;
			&:hover {
				color:white;
			}
		}
		&:hover {
			color:#99e9f2
		}
	}
`
const SideBottomBlock=styled.div`
	margin-top:40px;
	
	@media(max-width:768px) {
		display:flex;
		flex-direction:column;
		margin-top:15px;
		box-sizing:border-box;
	}
	.UserBlock {
		@media(max-width:768px) {
		}
	}
	.Text {
		font-size:14px;
		font-family: 'Crimson Text', serif;
		
	}
	.instagram {
		
	}
`
const MediaBottomBlock=styled.div`
	display:none;
	@media(max-width:768px) {
		display:flex;
		flex-wrap:wrap;
		margin-top:30px;
	.text {
		color:black;
		font-size:12px;
		margin-left:2px;
		padding: 2px 3px;
		cursor:pointer;
		transition:0.5s;
		&:hover {
			font-size:16px;
		}
	}
}
	
`
function Nav({Toggle}) {
	return (
		<PageBlock Toggle={Toggle}>
			<SidePageBlock>
				<SideBox>
					<UlBlock>
						<div className="list-item">전체보기</div>
						<div className="list-item">전체보기</div>
						<div className="list-item">전체보기</div>
						<div className="list-item">전체보기</div>
					</UlBlock>
					<SideBottomBlock>
						<div className="UserBlock">
							<img src= {user} alt="jun" />
						</div>
						<p className="Text">People can't do something themselves they wanna tell you<br /> you can't do it<br /> If you want somethin`, go get it </p>
						<p className="instagram">
							<img src={instagram} alt='instagram' />
							   &nbsp; wnsdud
						</p>
					</SideBottomBlock>
					<MediaBottomBlock>
						<div>
							<Link to ="/" style={{textDecoration:'none'}}><div className="text">홈으로</div></Link>
						</div>
						<div>
							<Link to ="/introduce" style={{textDecoration:'none'}}><div className="text">소개</div></Link>
						</div>
						<div className="text">
							포트폴리오
						</div>
						<div className="text">
							방명록
						</div>
						<div>
							<Link to ="Signin" style={{textDecoration:'none'}}><div className="text">관리자</div></Link>
						</div>
					</MediaBottomBlock>
				</SideBox>
			</SidePageBlock>
		</PageBlock>
	)
}

export default Nav
