import React,{useState}from 'react'
import styled,{createGlobalStyle} from 'styled-components'
import {Link} from 'react-router-dom'
function Signin() {
    const [email,Setemail] = useState('')
    const [password,Setpassword] = useState('')
    const onChangeEmail = e => {
        Setemail(e.target.value)
    }
    const onChangePassword = e => {
        Setpassword(e.target.value)
    }
    const GlobalStyle = createGlobalStyle`
        body {
            background:#e9ecef;
        }
        
    `

    const SigninBlock = styled.div`
        width:512px;
        height:760px;
        position:relative;
        background:white;
        border-radius:16px;
        bix-shadow:0 0 8px rgba(0,0,0,0.04);
        margin:0 auto;
        margin-top:96px;
        margin-bottom:32px;
        display:flex;
        flex-direction:column;
        .Header {
            padding:48px 32px 24px 32px;
            border-bottom:1px solid #e9ecef;

            .H1 {
                margin:0;
                font-size:36px;
                color:#343940;
            }
            .text {
                font-size: 18px;
                color:#206997;
                margin-top:40px;
                font-weight:bold;

            }
        }
        .Section {
            flex:1;
            padding:20px 30px;
            padding-bottom:48px;
            overflow-y:auto;
            .Inner {
                .form {
                    display:flex;
                    flex-direction:column;    
                    .label {
                        font-size:16px;
                        font-family: 'Roboto', sans-serif;
                    }
                    .input {
                        padding:6px;
                        border-radius:4px;
                        border:1px solid #dee2e6;
                        outline:none;
                        font-size:18px;
                        box-sizing:border-box;
                        &:hover {
                            color:#206997;
                        }
                    }
                    .btn {
                        
                    }
                }
            }
        }

    `
    const CircleButton = styled.div`
        background:#206997;
        opacity:0.8;
        &:hover {

            opacity:1;

        }
        cursor:pointer;
        width:400px;
        height:80px;
        border-radius:10px;
        display:flex;
        align-items:center;
        justify-content:center;
        position:absolute;
        left:50%;
        bottom:50%;
        transform:translate(-50%,50%);
        font-size:40px;
        
    `

    return (
        <>
            <GlobalStyle></GlobalStyle>
            <SigninBlock>
                <div className="Header">
                    <h1 className="h1">Sign In</h1>
                    <span className="text">관리자 로그인</span>
                </div>
                <div className="Section">
                    <div className="Inner">
                        <form className="form">
                            <label for ="inputemail" className="label">Email address</label>
                            <input 
                                type="email"
                                name ="email" 
                                className="input"
                                onChange ={onChangeEmail}
                                value={email}
                                 required autofocus />
                            <label for ="inputpassword" className="label">Password</label>
                            <input 
                                type="password"
                                className="input"
                                name="password"
                                onChange ={onChangePassword}
                                value={password}
                                 />
                            <CircleButton>Sign In</CircleButton>
                        </form>
                    </div>
                </div>
            </SigninBlock>
        </>
    )
}
export default Signin
