import React,{useState} from 'react'
import styled from 'styled-components'

function Comment() {

    const CommentBlock = styled.div`
        width:768px;
        display:flex;
        flex-direction:column;
        margin: 0 auto;
        background:#f1f4f8;
        border: 1px solid #DADDE0;
        @media(max-width:767px) {
            width:512px;
        
            
        }
        @media(max-width:540px) {
            width:420px;
            margin-left:20px;
            margin-right:20px;
            margin-top:20px;
            
        }
        @media(max-width:400px) {
            width:335px;
        }
        .form {
            display:flex;
            justify-content:space-between;
            padding:12px 20px 20px;
            .input {
                width:100%;
                height:36px;
                padding:0 11px;
                border:1px solid #dbdfe5;
                line-height:17px;
                color:#000;
                box-sizing:border-box;
            }
            .1 {

            }

            
        }
        
           

      

    `
    return (
        <>
            <CommentBlock>
                <div className="Background">
                    <form className="form">
                        
                        <input type="text" name="name" className="input 1" placeholder="필명" />
                        <input type="text" name="password" className="input 2" placeholder="확인번호" />
                    </form>
                    <textarea className="textarea" placeholder="댓글" style={{resize:'none'}} />
                </div>
            </CommentBlock>
        </>

    )
}
export default Comment
