import React ,{useState,useEffect} from 'react'
import styled from 'styled-components'
import axios from 'axios'
import {Link} from 'react-router-dom'
import ListIcon from './Icon/ListIcon.js'
const fetchURL = "http://localhost:5000/getPost"

function ShowPost() {
    const[items,SetItems] = useState()
    
    useEffect(() => {
        axios.post(fetchURL).then(res=> {
                console.log(res)
                console.log(res.data.length())
                SetItems(res.data)
            })
    },[])

    const ShowPostBlock = styled.div`
        width:100%;
        display:flex;
        flex-direction:column;
        .Header {
            display:flex;
            flex-direction:column;
            padding:30px 15px 30px;
            border-bottom:1px solid #ebebeb;
            margin-left:10px;
            cursor:pointer;
            @media(max-width:767px) {
                min-height:100px;
            }
            .title {
                font-size:18px;
                color:#080808;
                margin:8px 0px;
                font-weight:700;
                &:hover {
                    text-decoration:underline;
                }
            }
            .body {
                font-size:15px;
                color:#666666;
                overflow:hidden;
                lien-height:1.6;
            }
        }
        
    `
    return (
        <>
            <ShowPostBlock>
                {items
                ? items.map(item => {
                    return (
                        <div key={item.id}>
                            <div className="Header">
                                <span className="title">
                                    {item.title}
                                    {item._id}
                                    <Link to={`/PostPage/${item._id}`}>{item.title}</Link>
                                </span>
                                <span className="body">{item.body}</span>
                            </div>
                        </div>
                    )
                })
                : "Loading"
                }
            </ShowPostBlock>
            <ListIcon />
        </>
        
    )
}
export default ShowPost
