import React,{useState,useEffect} from 'react'
import styled from 'styled-components'
import axios from 'axios'

function Showpost() {
    const post = []
    useEffect(() => {
        axios.post('http://localhost:5000/getPost',{}).
        then(res => {
            console.log(res)
            post[res.data]
        })
        .catch(e=>console.log(e.response))
    },[post])

    return (

        <div>
            {post.body}
        </div>

    )
}

export default Showpost
