import React,{useState} from 'react'
import styled from 'styled-components'
import axios from 'axios'
const fetchURLCOM = 'http://localhost:5000/Comment'

function Comment() {
    const [name,SetName] = useState('')
    const [password,SetPassword] = useState('')
    const [body,SetBody] = useState('')
    const onChangename = e => {
        SetName(e.target.value)
    }
    const onChangepassword = e => {
        SetPassword(e.target.value)
    }
    const onChangeBody = e => {
        SetBody(e.target.value)
    }
    const onChange = (e) => {
        SetInput({
            ...input,
            [e.target.name]:e.target.value
        })
    }

    const onClick = () => {
        axios.post(fetchURLCOM,{
            name,
            password,
            body
        }).then(res=>console.log(res))
    }

    const CommentBlock = styled.div`
        width:768px;
        display:flex;
        flex-direction:column;
        margin: 0 auto;
        background:#f1f4f8;
        border: 1px solid #DADDE0;
        @media(max-width:767px) {
            width:512px;
        
            
        }
        @media(max-width:540px) {
            width:420px;
            margin-left:20px;
            margin-right:20px;
            margin-top:20px;
            
        }
        @media(max-width:400px) {
            width:335px;
        }
        .Background {
            .form {
                display:flex;
                justify-content:space-between;
                padding:12px 20px 20px;
                .input {
                    width:100%;
                    height:36px;
                    padding:0 11px;
                    border:1px solid #dbdfe5;
                    line-height:17px;
                    color:#000;
                    box-sizing:border-box;
                    margin: 0 4px;
                }
            }
            .textarea1 {
                padding:0px 20px 0px;
                .textarea {
                    width:100%;
                    height:100px;
                    padding: 11px;
                    border:1px solid #dbdfe5;
                    box-sizing:border-box;
                }
            }
            .Submit {
                text-align:right;
                padding:5px 5px 10px;
                .btn {
                    width:70px;
                    height:32px;
                    background:#bec5d0;
                    outline:none;
                    border:none;
                    border-radius:32px;
                    font-size:14px;
                    color:#fff;
                    line-height:31px;
                    
                }
            }
        }
        
        
           

      

    `
    return (
        <>
            <CommentBlock>
                <div className="Background">
                    <form className="form">
                        
                        <input type="text" value={name} className="input 1" placeholder="필명" onChange = {onChangeName} />
                        <input type="text" value={password} className="input 2" placeholder="확인번호" onChange={onChangepassword} />
                    </form>
                    <div className="textarea1">
                        {name}
                        <textarea value={body} className="textarea" placeholder="댓글을 입력해주세요" onChange={onChangeBody} />
                    </div>
                    <div className="Submit">
                        <button type="submit" className="btn" onClick={onClick}>제출</button>
                    </div>
                </div>
            </CommentBlock>
        </>

    )
}
export default Comment
